	@Override
	public void mutateReverse() {
		for (String str : reversals) {
			char ct = str.charAt(0);
			char pt = str.charAt(1);
			map(ct, pt, false);
		}
		mutateReverseClear();
	}
	@Override
	public void mutateReverseClear() {
		reversals.clear();
	}

	@Override
	public String representation() {
		String cipher = "";
		if (energyCached <= 10) {
			cipher = HomophonicGenerator.makeHomophonic(ctCounts, p2c, plaintext);
			NGramsBean ng2 = new NGramsBean(2, cipher);
			NGramsBean ng3 = new NGramsBean(3, cipher);
			NGramsBean ng4 = new NGramsBean(4, cipher);
			cipher += ", " + ng2.numRepeats() + ", " + ng3.numRepeats() + ", " + ng4.numRepeats();
		}
		return energyCached + "	" + p2c + ", " + c2p + ", plaintext: " + plaintext + ", cipher: " + cipher;
	}

	@Override
	public double energyCached() {
		// TODO Auto-generated method stub
		return energyCached;
	}

	@Override
	public void initialize() {
		inputCipher = Ciphers.Z340;

		//plaintext = Ciphers.Z408_SOLUTION.substring(0, 340);
		plaintext = plaintexts[random.nextInt(plaintexts.length)];
		
		alphabetPt = Ciphers.alphabet(plaintext);
		alphabetCt = Ciphers.alphabet(inputCipher);
		reversals = new ArrayList<String>();
		p2c = new HashMap<Character, Set<Character>>();
		c2p = new HashMap<Character, Character>();
		initializeCounts();
		for (Character ct : ctCounts.keySet()) {
			char pt = randomPt();
			map(ct, pt, false);
		}
	}
	public void initializeCounts() {
		ctCounts = Ciphers.countMap(inputCipher);
		ptCounts = Ciphers.countMap(plaintext);
	}
	
	/** map this ct to the given pt (and vice versa).  removes the old assignment. */
	boolean map(char cNew, char pNew, boolean addToReversals) {
		if (!alphabetCt.contains(""+cNew))
			throw new RuntimeException("WTF cipher alphabet doesn't contain " + cNew + "!  " + alphabetCt);
		// remove old pt-ct mapping
		if (c2p == null) System.out.println("c2p IS NULL");
		
		if (c2p.containsKey(cNew)) {
			char pOld = c2p.get(cNew);
			if (addToReversals) addToReversals(cNew, pOld);
			p2c.get(pOld).remove(cNew);
		}
		c2p.remove(cNew);
		// remove old ct-pt mapping
		
		// map pt to ct
		Set<Character> val = p2c.get(pNew);
		if (val == null) val = new HashSet<Character>();
		val.add(cNew);
		p2c.put(pNew, val);
		// map ct to pt
		c2p.put(cNew, pNew);
		return true;
	}

	public GeneratorSolution() {
	}
	
	public char randomCt() {
		return alphabetCt.charAt(random.nextInt(alphabetCt.length()));
	}
	public char randomPt() {
		return alphabetPt.charAt(random.nextInt(alphabetPt.length()));
	}
	
	void addToReversals(char c, char p) {
		reversals.add(c + "" + p);
	}
	
	@Override
	public boolean mutate() {
		
		// 1) move a random ct to another pt
		if (random.nextBoolean()) {
			char c = randomCt();
			char p = randomPt();
			map(c, p, true);
			
		} else {
		// 2) pick two random ct and swap them
			char c1 = randomCt();
			char c2 = c1;
			while (c1 == c2) c2 = randomCt();
			
			char p1 = c2p.get(c1);
			char p2 = c2p.get(c2);
			
			map(c1, p2, true);
			map(c2, p1, true);
		}
		return true;
	}
	
	@Override
	public double energy() {
		energyCached = 0;
		// add up differences
		for (int i=0; i<alphabetPt.length(); i++) {
			char pt = alphabetPt.charAt(i);
			Set<Character> cts = p2c.get(pt);
			if (cts == null) continue;
			int ptCount = ptCounts.get(pt);
			int ctCount = 0;
			
			for (Character ct : cts) {
				
				if (ctCounts.get(ct) == null) {
					System.out.println(ctCounts);
					System.out.println(ptCounts);
					System.out.println(ct + " " + alphabetCt);
				}
				
				ctCount += ctCounts.get(ct);
			}
			energyCached += Math.abs(ptCount - ctCount);
		}
		
		
		return energyCached;
	}
	
	@Override
	public Solution clone() {
		GeneratorSolution newSol = new GeneratorSolution();
		newSol.plaintext = this.plaintext;
		newSol.inputCipher = this.inputCipher;
		newSol.p2c = new HashMap<Character, Set<Character>>();
		for (Character key : p2c.keySet()) {
			Set<Character> val = new HashSet<Character>();
			val.addAll(p2c.get(key));
			newSol.p2c.put(key, val);
		}
		newSol.c2p = new HashMap<Character, Character>();
		for (Character key : c2p.keySet()) {
			newSol.c2p.put(key, c2p.get(key));
		}
		initializeCounts();
		newSol.reversals = new ArrayList<String>();
		newSol.reversals.addAll(reversals);
		
		return newSol;
	}
	
	public static void testIoc() {
		for (String pt : plaintexts) {
			System.out.println(Stats.ioc(pt) + " " + pt);
		}
	}
	
	public static void main(String[] args) {
		testIoc();
	}

