	public static void testShifts() {
		String alpha = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
		for (int N=1; N<=13; N++) {
			System.out.print(N + " ");
			for (int i=0; i<alpha.length(); i++) {
				char ch = alpha.charAt(i);
				System.out.print(ch+""+shift(ch,-N)+""+shift(ch,N) + " ");
			}
			System.out.println();
		}
	}
	public static void compareJarlve() {
		for (int i=0; i<plaintexts.length; i++) {
			String pt1 = plaintexts[i].toUpperCase();
			String pt2 = plaintextsJarlve[i].toUpperCase();
			if (pt2.length() == 0) continue;
			System.out.println("===== " + (i+1) + " =====");
			System.out.println("Jarl's:");
			System.out.println(pt2);
			System.out.println("Actual:");
			System.out.println(pt1);
			float match = 0;
			for (int j=0; j<pt1.length(); j++) if (pt1.charAt(j) == pt2.charAt(j)) match++;
			match /= pt1.length();
			match *= 100;
			System.out.println("Accuracy: " + match + "%");
			
		}
	}
