	public static void testNonOverlapping(int n, String cipher) {
		System.out.println(StatsWrapper.header());
		int SHUFFLES = 10000;
		
		for (int start=0; start<n; start++) {
			System.out.println("SHUFFLE NGRAM REPEAT TESTS, N=" + n + " OFFSET=" + start);
			
			StringBuffer sb = new StringBuffer();
			
			for (int i=start; i<cipher.length()-n+1; i+=n) {
				sb.append(cipher.substring(i,i+n));
			}

			String shuffled = sb.toString();
			NGramsBean bean = new NGramsBean(n, sb.toString());
			StatsWrapper stats = new StatsWrapper();
			stats.name = "n=" + n;
			stats.actual = bean.numRepeats();
				
			for (int i=0; i<SHUFFLES; i++) {
				shuffled = CipherTransformations.shuffle(shuffled);
				bean = new NGramsBean(n, shuffled);
				stats.addValue(bean.numRepeats());
			}
			stats.output();
		}
	}
	
	// too many or too few occurrences of sequences?
	static void countTest(String cipher, int length) {
		String tab = "	";
		
		// symbol frequencies
		Map<Character, Integer> freq = Ciphers.countMap(cipher);
		
		
		Map<String, Integer> counts = new HashMap<String, Integer>();
		for (int i=0; i<cipher.length()-length+1; i++) {
			String key = cipher.substring(i,i+length);
			Integer val = counts.get(key);
			if (val == null) val = 0;
			val++;
			counts.put(key, val);
		}
		
		for (int i=0; i<Math.pow(10, length); i++) {
			String key = padLeftZeros(""+i, length);
			
			Integer count = counts.get(key);
			if (count == null) count = 0;
			
			float expected = expected(freq, key, cipher.length());
			float diff = (count-expected)/expected;
			
			System.out.println(key + tab + count + tab + expected + tab + diff);
		}
	}
	
	// return expected number of occurrences of the given string with the given symbol frequencies, with the given sample length
	static float expected(Map<Character, Integer> freqs, String str, int length) {
		float expected = 1;
		for (int i=0; i<str.length(); i++) {
			char c = str.charAt(i);
			float freq = freqs.get(c);
			freq /= length;
			expected *= freq;
		}
		return expected * length;
	}
	
	public static String padLeftZeros(String inputString, int length) {
	    if (inputString.length() >= length) {
	        return inputString;
	    }
	    StringBuilder sb = new StringBuilder();
	    while (sb.length() < length - inputString.length()) {
	        sb.append('0');
	    }
	    sb.append(inputString);

	    return sb.toString();
	}
	
	
	// get the distribution of differences between successive digits
	public static Map<Integer, Integer> digitDifferences(String cipher, boolean absoluteValue) {
		Map<Integer, Integer> counts = new HashMap<Integer, Integer>();
		Integer previousDigit = 0;
		for (int i=0; i<cipher.length(); i++) {
			char c = cipher.charAt(i);
			if (Character.isDigit(c) ) {
				Integer digit = Integer.valueOf(""+c);
				if (i==0) {
				} else {
					Integer diff = digit - previousDigit;
					if (absoluteValue) diff = Math.abs(diff);
					Integer val = counts.get(diff);
					if (val == null) val = 0;
					val++;
					counts.put(diff, val);
					//System.out.println(digit + " " + previousDigit + " " + diff);
				}
				previousDigit = digit;
				
			}
		}
		return counts;
	}
	
	public static void testDigitDifferences(String cipher, boolean absoluteValue, int shuffles) {
		Map<Integer, Integer> counts = digitDifferences(cipher, absoluteValue);
		System.out.println(counts);
		
		Map<Integer, StatsWrapper> statsMap = new HashMap<Integer, StatsWrapper>(); 
		for (int i=0; i<10; i++) {
			StatsWrapper stats = new StatsWrapper();
			stats.name = "diff: " + i;
			stats.actual = counts.get(i);
			statsMap.put(i, stats);
			if (i > 0 && !absoluteValue) {
				stats = new StatsWrapper();
				stats.name = "diff: " + -i;
				stats.actual = counts.get(-i);
				statsMap.put(-i, stats);
			}
		}
		
		String shuffle = cipher;
		for (int j=0; j<shuffles; j++) {
			shuffle = CipherTransformations.shuffle(shuffle);
			counts = digitDifferences(shuffle, absoluteValue);
			for (Integer key : statsMap.keySet()) {
				Integer val = counts.get(key);
				if (val == null) val = 0;
				statsMap.get(key).addValue(val);
			}
		}
		
		for (StatsWrapper stats : statsMap.values()) {
			stats.output();
		}
	}
	
	// get the distribution of differences between successive digits - generalized to sequences of the given length
	public static Map<String, Integer> digitDifferences(String cipher, boolean absoluteValue, int sequenceLength) {
		Map<String, Integer> counts = new HashMap<String, Integer>();
		
		int total = 0;
		
		for (int i=sequenceLength; i<cipher.length(); i++) {
			
			String sequence = "";
			//String debug = "";
			for (int j=i-sequenceLength; j<i; j++) {
				//debug += "" + cipher.charAt(j) + cipher.charAt(j+1);
				//System.out.println(debug);
				int digit1 = Integer.valueOf(""+cipher.charAt(j));  
				int digit2 = Integer.valueOf(""+cipher.charAt(j+1));
				
				int diff = digit2-digit1;
				if (sequence.length() > 0)
					sequence += " ";
				sequence += diff;
			}
			
			total++;
			
			Integer count = counts.get(sequence);
			if (count == null) count = 0;
			count++;
			counts.put(sequence, count);
			
		}
		return counts;
	}
	
	public static void testDigitDifferenceSequenceShuffle(String cipher, int[] sequence, int shuffles) {
		StatsWrapper stats = new StatsWrapper();
		stats.name = Arrays.toString(sequence);
		stats.actual = countDigitDifferenceSequence(cipher, sequence);
		
		for (int i=0; i<shuffles; i++) {
			String shuffle = CipherTransformations.shuffle(cipher);
			stats.addValue(countDigitDifferenceSequence(shuffle, sequence));
		}
		stats.output();
	}
	
	public static int countDigitDifferenceSequence(String cipher, int[] sequence) {
		int count = 0;
		for (int i=0; i<cipher.length()-sequence.length; i++) {
			boolean hit = true;
			for (int j=0; j<sequence.length; j++) {
				int a=Integer.valueOf(""+cipher.charAt(i+j));
				int b=Integer.valueOf(""+cipher.charAt(i+j+1));
				//System.out.println(b-a);
				if (sequence[j] != (b-a)) {
					hit = false;
					break;
				}
			}
			if (hit) count++;
		}
		return count;
	}
	
	
	
