	public static void averageSymbolDistanceShuffleTestWindows(String cipher, int windowSize, boolean overlap) {
		int step = overlap ? 1 : windowSize;
		for (int i = 0; i < cipher.length(); i += step) {
			if (i >= cipher.length() - windowSize + 1)
				break;
			String sub = cipher.substring(i, i + windowSize);
			System.out.println("=== window starting at " + i);
			for (int j=0; j<10; j++) {
				averageSymbolDistanceShuffleTest(sub, ""+j, 10000);
			}
		}
	}
	

