	/** iterative word break finder.  at each step, try a space at every position.  whichever position
	 * produces the best score is kept.  repeat until no further improvements are possible.
	 */
	public static StringBuffer findWordBreaks(StringBuffer input, String langID) {
		return findWordBreaks(input, langID, false);
	}
	public static StringBuffer findWordBreaks(StringBuffer input, String langID, boolean returnScore) {
		input = breakNumbers(input);
		double best = 0;
		while(true) {
			double current = ZKDecrypto.calcscore(input, langID, true);
			best = current;
			StringBuffer bestInput = input;
			for (int i=1; i<input.length(); i++) {
				StringBuffer newInput = new StringBuffer(input);
				newInput.insert(i, ' ');
				double newScore = ZKDecrypto.calcscore(newInput, langID, true);
				if (newScore > best) {
					best = newScore;
					bestInput = newInput;
				}
			}
			if (best > current) {
				input = bestInput;
			} else {
				break;
			}
		}
		if (returnScore) {
			input = new StringBuffer(best + "	" + input);
		}
		return input;
	}
	
	public static StringBuffer breakNumbers(StringBuffer input) {
		StringBuffer result = new StringBuffer();
		result.append(input.charAt(0));
		for (int i=1; i<input.length(); i++) {
			char cBefore = input.charAt(i-1);
			char c = input.charAt(i);
			
			boolean b1 = Character.isDigit(cBefore);
			boolean b2 = Character.isDigit(c);
			
			// if there was a transition, insert a space.
			if (b1^b2) {
				result.append(" ");
			}
			result.append(c);
		}
		return result;
	}
	
