	public static void findWordBreaks(String fileName) {
		String lang = "EN";
		init(lang, true);
		System.out.println("Loading strings...");
		List<String> lines = FileUtil.loadFrom(fileName);
		for (String line : lines) {
			String withoutSpaces = line.replaceAll(" ", "").toUpperCase();
			StringBuffer result = findWordBreaks(new StringBuffer(withoutSpaces), lang, true);
			System.out.println("   Test string: " + withoutSpaces);
			System.out.println("Guessed breaks: " + result);
		}
	}
	
	public static void testCompatibleCribs() {
		init("EN", false);
		String path = "/Volumes/My Passport - 4TB/projects/zodiac/findCompatibleCribs-3.txt";
		BufferedReader input = null;
		long counter = 0;
		long start = new Date().getTime();
		
		
		try {
//			input = new BufferedReader(new FileReader(new File(path)));
			input = new BufferedReader(
			           new InputStreamReader(new FileInputStream(path), "UTF-8"));
			
			String line = null; // not declared within while loop
			while ((line = input.readLine()) != null) {
				counter++;
				if (line.startsWith("loading")) continue;
				if (line.startsWith("read")) continue;
				String[] split = line.split("	");
				Double ratio = Double.valueOf(split[0]);
				Double zkscore = Double.valueOf(split[1]);
				
				if (zkscore >= 4474.392 || ratio >= 0.5) {
					String pt = split[3].replaceAll("_", "");
					float msPerCount = new Date().getTime() - start;
					msPerCount /= counter;
					
					System.out.println(findWordBreaks(new StringBuffer(pt), "EN", true) + "	" + ratio + "	" + zkscore
							+ "	" + counter + "	" + msPerCount);
				}
			}
			//System.out.println("read " + counter + " lines.");
		} catch (FileNotFoundException ex) {
			ex.printStackTrace();
		} catch (IOException ex) {
			ex.printStackTrace();
		} 
		
		try {
			input.close();
		} catch (IOException e1) {
			e1.printStackTrace();
		}
		
	}
	/** calculate levenshtein edit distance.  from https://www.baeldung.com/java-levenshtein-distance */
	public static int editDistance(String x, String y) {
		int[][] dp = new int[x.length() + 1][y.length() + 1];
		for (int i = 0; i <= x.length(); i++) {
			for (int j = 0; j <= y.length(); j++) {
				if (i == 0) {
					dp[i][j] = j;
				} else if (j == 0) {
					dp[i][j] = i;
				} else {
					dp[i][j] = min(dp[i - 1][j - 1] + costOfSubstitution(x.charAt(i - 1), y.charAt(j - 1)),
							dp[i - 1][j] + 1, dp[i][j - 1] + 1);
				}
			}
		}
		return dp[x.length()][y.length()];
	}

	public static int min(int... numbers) {
		return Arrays.stream(numbers).min().orElse(Integer.MAX_VALUE);
	}

	public static int costOfSubstitution(char a, char b) {
        return a == b ? 0 : 1;
    }	
	
